let guti_lookback_duration_days = 7;
// A rnti will have been created shortly before the GUTI mapping is seen. Therefore times are adjusted accordinly.
let rnti_guti_tolerance_secs = 3;

// capture the main data
let data = Janus_CL
| where stream_type_s == "janus"
    and stream_context_info_codelet_set_id_s == "fapi_gnb_dl_config_stats"
| project stream_payload_time_t, p=todynamic(stream_payload_msg_s)
| project stream_payload_time_t, stats=p.stats
| order by stream_payload_time_t asc
| mv-expand stats
| project stream_payload_time_t, cell_id=todouble(stats.cellId), rnti=tostring(stats.rnti), tx=todouble(stats.l1DlcTx)
| where toint(rnti) >= 20000 and toint(rnti) <= 22000;
// calulate the latest time in data
let t_end = toscalar(data | summarize max_time = max(stream_payload_time_t));

// create table of imsi_stmsi mappings from <guti_lookback_duration_days> days before start of query time range
let guti_suci_start_time = datetime_add('day', -guti_lookback_duration_days, {TimeRange:start});
let imsi_stmsi = PcapF1AP_CL
| where stream_payload_structure_s == "5GNAS:GUTI/SUCI-Event" and stream_payload_time_t >= guti_suci_start_time and stream_payload_time_t <= t_end
| order by stream_payload_time_t asc
| project stream_payload_time_t, msg=todynamic(stream_payload_msg_s)
| project stream_payload_time_t=datetime_add('second', -rnti_guti_tolerance_secs, stream_payload_time_t), stmsi=tostring(todynamic(msg.guti)["5g_stmsi_hex"]), imsi=tostring(todynamic(msg.suci).imsi)
| extend stmsi_imsi = strcat(stmsi, "/", imsi);

// create table of stmsi/crnti/nrcgi/pci mappings by looking at the STMSI_ASSIGN events
let stmsi_crnti_nrcgi_pci = PcapF1AP_CL
| where stream_payload_structure_s == "pcapDecoderF1AP:Report"  and stream_payload_time_t >= guti_suci_start_time and stream_payload_time_t <= t_end
| project stream_payload_time_t, msg=todynamic(stream_payload_msg_s)
| project stream_payload_time_t, events=todynamic(msg.ue_events), ue=todynamic(msg.ue)
| where isnotnull(events) and isnotnull(ue)
| mv-expand events
| where events.event == "5G_STMSI_ASSIGN"
| mv-expand ue.ids.contexts
| project stream_payload_time_t=datetime_add('second', -rnti_guti_tolerance_secs, stream_payload_time_t), stmsi=tostring(ue.ids.5g_stmsi_hex), rnti=tostring(ue_ids_contexts.crnti), nrcgi=tostring(ue_ids_contexts.nrcgi), cell_id=todouble(ue_ids_contexts.pci)
| distinct *;

// add the imsi to the stmsi/crnti/nrcgi/pci mappings table
let stmsi_crnti_nrcgi_pci_imsi = stmsi_crnti_nrcgi_pci
| join kind=inner (imsi_stmsi | project stmsi, imsi) on stmsi
| distinct *;


// Add nearest latest timestamp from stmsi_crnti_nrcgi_pci_imsi i.e. 
//
// if "stmsi_crnti_nrcgi_pci_imsi" table has this ..
//  time   cell_id    rnti   ...   imsi
//  13:00       11   20000            1
//  14:00       11   20000            2
//  15:00       11   20000            3
//
// and "data" table has this ..
//  time   cell_id    rnti   ...   tx
//  12:55       11   20000          1
//  13:00       11   20000          2
//  13:55       11   20000          3
//  14:05       11   20000          4
//  14:55       11   20000          5
//  15:00       11   20000          6
//
// the resulting table should be ..
//  time   cell_id    rnti   ...   tx    imsi
//  12:55       11   20000          1
//  13:00       11   20000          2       1
//  13:55       11   20000          3       1
//  14:05       11   20000          4       2
//  14:55       11   20000          5       2
//  15:00       11   20000          6       3

let data_imsi_union = data
| join kind=leftouter (
    stmsi_crnti_nrcgi_pci_imsi
    | project imsi_time = stream_payload_time_t, stmsi, rnti, cell_id, imsi
) on rnti, cell_id
| order by stream_payload_time_t asc;

let data_with_imsi = data_imsi_union
| where stream_payload_time_t >= imsi_time
| order by stream_payload_time_t asc
| summarize arg_max(imsi_time, imsi) by stream_payload_time_t, rnti, cell_id, tx
| project stream_payload_time_t, rnti, cell_id, tx, imsi
| extend ueid=strcat("imsi=",imsi)
| order by stream_payload_time_t asc;

