| where (stream_payload_time_t < imsi_time) or isnull(imsi_time)
| project-away imsi, imsi_time
| extend ueid=strcat("rnti=",rnti)
| summarize arg_min(stream_payload_time_t, *) by stream_payload_time_t
| order by stream_payload_time_t asc;

let data_final = data_without_imsi
| union kind=outer data_with_imsi
| summarize arg_max(imsi, *) by stream_payload_time_t, cell_id, rnti
| order by stream_payload_time_t asc;

data_final
| summarize tx_Bps=sum(tx*8/1000000) by bin(stream_payload_time_t, 1s), ueid
| render timechart with (ycolumns=tx_Bps, series=ueid, ytitle="Downlink throughput [Mbps]")

